mapper.select:
select *
from tb_biz_meta_name tbmn
where name_id = #{nameId}

mapper.update:
update tb_biz_meta_name
set kor_name = #{korName},
	eng_name = #{engName},
	show_order = #{showOrder},
	type= #{type}
where name_id = #{nameId}

update.foreach:
	   update tb_biz_meta 
       set item_val = #{item.itemVal}
       where biz_dataset_id= #{bizDatasetId} and item_id= #{item.itemId}

mapper.select:
select *
from metasch.tb_category_test
order by depth, parent_id, node_id;
